import React from 'react';
import { Home, Cloud, BarChart, FileText, Info, HelpCircle } from 'lucide-react'; // Sidebar icons
import '../styles.css';

const Sidebar = ({ isSidebarOpen }) => {
  return (
    <aside className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
      <ul className="menu">
        <li className="menu-item">
          <Home size={20} />
          {isSidebarOpen && <span>Home</span>}
        </li>
        <li className="menu-item">
          <BarChart size={20} />
          {isSidebarOpen && <span>Crop Market</span>}
        </li>
        <li className="menu-item">
          <FileText size={20} />
          {isSidebarOpen && <span>Govt. P&S</span>}
        </li>
        <li className="menu-item">
          <Cloud size={20} />
          {isSidebarOpen && <span>Weather</span>}
        </li>
        <li className="menu-item">
          <Info size={20} />
          {isSidebarOpen && <span>About Us</span>}
        </li>
        <li className="menu-item">
          <HelpCircle size={20} />
          {isSidebarOpen && <span>Help</span>}
        </li>
      </ul>
    </aside>
  );
};

export default Sidebar;
